import React, { useState, useEffect } from 'react';

const slides = [
  {
    id: 1,
    title: 'Shubho Mahalaya Offer',
    subtitle: '25% OFF',
    code: 'DURGA25',
    description: 'Thekua ka swaad, Maa Durga ka aashirwad!',
    imageUrl: '/images/thekua1.png',
    ctaText: 'Order Now',
    ctaLink: '/products',
  },
  {
    id: 2,
    title: 'Special Coconut Jaggery Magic',
    subtitle: '',
    code: '',
    description: 'Authentic taste with no additives',
    imageUrl: '/images/thekua2.png',
    ctaText: 'Shop Now',
    ctaLink: '/products',
  },
  // Add more slides as needed
];

const Carousel = () => {
  const [current, setCurrent] = useState(0);
  const length = slides.length;

  useEffect(() => {
    const timer = setTimeout(() => {
      setCurrent((current + 1) % length);
    }, 5000);
    return () => clearTimeout(timer);
  }, [current, length]);

  return (
    <div className="relative w-full h-64 sm:h-96 overflow-hidden rounded-lg shadow-lg mt-8">
      {slides.map((slide, index) => (
        <div
          key={slide.id}
          className={`absolute inset-0 transition-opacity duration-1000 ease-in-out ${
            index === current ? 'opacity-100 z-10' : 'opacity-0 z-0'
          }`}
        >
          <img
            src={slide.imageUrl}
            alt={slide.title}
            className="w-full h-full object-cover"
          />
          <div className="absolute inset-0 bg-black bg-opacity-30 flex flex-col justify-center items-start p-6 sm:p-12 text-white">
            <h2 className="text-3xl font-extrabold">{slide.title}</h2>
            {slide.subtitle && (
              <p className="text-xl font-semibold mt-2">{slide.subtitle}</p>
            )}
            {slide.code && (
              <p className="mt-1 bg-yellow-500 text-black px-2 py-1 rounded inline-block font-bold">
                Use Code: {slide.code}
              </p>
            )}
            <p className="mt-3 max-w-md">{slide.description}</p>
            <a
              href={slide.ctaLink}
              className="mt-4 inline-block bg-yellow-600 hover:bg-yellow-700 text-black font-bold py-2 px-4 rounded"
            >
              {slide.ctaText}
            </a>
          </div>
        </div>
      ))}
      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
        {slides.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrent(index)}
            className={`w-3 h-3 rounded-full ${
              index === current ? 'bg-yellow-600' : 'bg-yellow-300'
            }`}
            aria-label={`Go to slide ${index + 1}`}
          />
        ))}
      </div>
    </div>
  );
};

export default Carousel;
