import React, { useState } from 'react';
import ProductGrid from '../components/ProductGrid';

const products = [
  {
    id: 1,
    name: 'Butter Chicken Meal',
    imageUrl: '/images/product1.png',
    price: '₹350',
    description: 'Creamy and rich butter chicken with naan.',
    fullDescription: 'Savor the authentic taste of our Butter Chicken Meal, featuring tender chicken cooked in a creamy tomato-based sauce with aromatic spices. Served with fresh naan bread and a side of rice.',
    ingredients: ['Chicken', 'Cream', 'Tomatoes', 'Spices', 'Naan', 'Rice'],
  },
  {
    id: 2,
    name: 'Paneer Tikka Masala',
    imageUrl: '/images/product2.png',
    price: '₹280',
    description: 'Spicy paneer tikka in rich gravy.',
    fullDescription: 'Our Paneer Tikka Masala combines marinated paneer cubes grilled to perfection with a spicy, tangy masala sauce. A vegetarian delight that\'s full of flavor.',
    ingredients: ['Paneer', 'Yogurt', 'Spices', 'Tomatoes', 'Cream'],
  },
  {
    id: 3,
    name: 'Dal Makhani Combo',
    imageUrl: '/images/product3.png',
    price: '₹220',
    description: 'Slow-cooked lentils with rice and roti.',
    fullDescription: 'Indulge in our Dal Makhani Combo, featuring slow-cooked black lentils simmered with cream and spices. Served with jeera rice and butter roti for a complete meal.',
    ingredients: ['Black Lentils', 'Cream', 'Spices', 'Rice', 'Roti'],
  },
  {
    id: 4,
    name: 'Family Thali',
    imageUrl: '/images/product4.png',
    price: '₹500',
    description: 'Assorted dishes for the whole family.',
    fullDescription: 'Our Family Thali offers a variety of home-style dishes including vegetable curry, dal, rice, roti, and a sweet. Perfect for sharing and experiencing multiple flavors.',
    ingredients: ['Mixed Vegetables', 'Lentils', 'Rice', 'Roti', 'Spices', 'Sweet'],
  },
  {
    id: 5,
    name: 'Thekua Sweet',
    imageUrl: '/images/thekua.png', // Add the image of Thekua sweet here
    price: '₹150',
    description: 'Traditional sweet snack from Bihar.',
    fullDescription: 'Thekua is a traditional sweet snack from Bihar, made with wheat flour, jaggery, and ghee. It is deep-fried to perfection, offering a crispy and delightful treat.',
    ingredients: ['Wheat Flour', 'Jaggery', 'Ghee', 'Cardamom'],
  },
];

const Products = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredProducts, setFilteredProducts] = useState(products);

  const handleSearch = (term) => {
    setSearchTerm(term);
    const filtered = products.filter((product) =>
      product.name.toLowerCase().includes(term.toLowerCase()) ||
      product.description.toLowerCase().includes(term.toLowerCase())
    );
    setFilteredProducts(filtered);
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-4xl font-extrabold mb-8 text-center">Our Products</h1>

      {/* Search Bar */}
      <div className="mb-8 flex justify-center">
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => handleSearch(e.target.value)}
          className="w-full max-w-md px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-500"
        />
      </div>

      {/* Product Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
        {filteredProducts.map((product) => (
          <div
            key={product.id}
            className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-xl transition-shadow duration-300"
          >
            <img
              src={product.imageUrl}
              alt={product.name}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h3 className="text-lg font-semibold">{product.name}</h3>
              <p className="text-yellow-700 font-bold mt-1">{product.price}</p>
              <p className="text-gray-600 mt-2 text-sm">{product.description}</p>
              <button className="mt-4 w-full bg-yellow-600 hover:bg-yellow-700 text-black font-bold py-2 rounded transition-colors duration-200">
                Add to Cart
              </button>
            </div>
          </div>
        ))}
      </div>

      {filteredProducts.length === 0 && (
        <p className="text-center text-gray-500 mt-8">No products found matching your search.</p>
      )}
    </div>
  );
};

export default Products;
